package day6

import (
	"testing"
)

var (
	testInputs = []struct {
		input string
		v1    int
		v2    int
	}{
		{"mjqjpqmgbljsphdztnvjfqwrcgsmlb", 7, 19},
		{"bvwbjplbgvbhsrlpgdmjqwftvncz", 5, 23},
		{"nppdvjthqldpwncqszvftbrmjlhg", 6, 23},
		{"nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 10, 29},
		{"zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 11, 26},
	}

	realInput = ``
)

func skeptSequence(input string, length int) int {
SequenceSearch:
	for i := 0; i < len(input); i++ {
		dedup := make(map[byte]bool)
		for skept := 0; skept < length; skept++ {
			c := input[i+skept]
			if dedup[c] == true {
				continue SequenceSearch
			}
			dedup[c] = true
			if skept == length-1 {
				return i + length
			}
		}
	}
	panic("sequence not found")
}

func TestDay6Phase1(t *testing.T) {
	for _, test := range testInputs {
		if test.v1 != skeptSequence(test.input, 4) {
			t.Fail()
		}
	}
	res := skeptSequence(realInput, 4)
	if res != 1876 {
		t.Fail()
	}
}

func TestDay6Phase2(t *testing.T) {
	for _, test := range testInputs {
		if test.v2 != skeptSequence(test.input, 14) {
			t.Fail()
		}
	}
	res := skeptSequence(realInput, 14)
	if res != 2202 {
		t.Fail()
	}
}
